import axiosInstance from '../axios';

const CHAT_API = {
    newChat: "/api/v1/chats",
    addUser: "/api/v1/chats/:id/users",
}

export const createNewChat = (params) => {
    let url = `${CHAT_API.newChat}`;
    return axiosInstance.post(url, params);
}

export const addUserToChat = (chatID, params) => {
    let url = `${CHAT_API.addUser.replace(':id', chatID)}`;
    return axiosInstance.post(url, params);
}
